import functools
class Solution:
    def splitString(self, s: str) -> bool:
        @functools.cache
        def dfs(ss: str, target: int) -> bool:
            if int(ss) == target: return True
            for i in range(1, len(ss)):
                curr, nxt = int(ss[:i]), ss[i:]
                if target == curr and dfs(nxt, curr - 1): return True
            return False

        for i in range(1, len(s)):
            curr, nxt = int(s[:i]), s[i:]
            if dfs(nxt, curr - 1): return True
        return False
