class Solution {
    int count = 0;
    public boolean splitString(String s) {
        if (s.length() < 2) {
            return false;
        } else if (s.length() == 2) {
            if (Integer.valueOf(s.charAt(0)) - Integer.valueOf(s.charAt(1)) == 1) {
                return true;
            }
            return false;
        }
        for (int i = 1; i < s.length(); i++) {
            double prev = Double.valueOf(s.substring(0, i)); 
            helper(s, i, prev); 
        }
        return count >= 1;
    }

    private void helper (String s, int index, double prev) {
        if (index == s.length()) {
            count++;
            return;
        }
        for (int i = 1; i <= s.length() - index; i++) {
            double curr = Double.valueOf(s.substring(index, index + i));
            if (prev - curr == 1) {
                helper(s, index + i, curr);
            } 
        }
    }
}
