import java.math.*;
class Solution {
    public boolean splitString(String s) {
      // I have used BigInteger over here because the long limit will break when the string length gets bigger
        return splitString(new BigInteger("-1"), s, 0);
    }

    private boolean splitString(BigInteger prevNum, String s, int part) {
      if (s.isEmpty() && part > 1) return true;
      for (int i = 0; i < s.length(); i++) {
        var currentNum = new BigInteger(s.substring(0, i + 1));
        if ((part == 0 || prevNum.subtract(currentNum).equals(new BigInteger("1"))) && splitString(currentNum, s.substring(i + 1), part + 1)) {
          return true;
        }
      }
      return false;
    }
}
