class Solution {
public:
    string Trimmer(string &str){  // to remove trailing zeros
        int i=0;
        while(i<str.size() && str[i]=='0'){
            i++;
        }
        return str.substr(i);
    }

    bool isSmaller(string str1, string str2){
        str1=Trimmer(str1);
        str2=Trimmer(str2);

        int n1=str1.size();
        int n2=str2.size();

        if(n1<n2){
            return true;
        }
        if(n2<n1){
            return false;
        } 

        for(int i=0;i<n1;i++){
            if(str1[i]<str2[i]){
                return true;
            }
            if(str1[i]>str2[i]){
                return false;
            }
        }
        return false;
    }

    string findDiff(string str1, string str2){
        if(isSmaller(str1,str2)==true){
            return "-1";
        }

        str1=Trimmer(str1);
        str2=Trimmer(str2);

        string str="";

        int n1=str1.size();
        int n2=str2.size();

        reverse(str1.begin(),str1.end());
        reverse(str2.begin(),str2.end());

        int carry=0;
        for(int i=0;i<n2;i++){
