class Solution(object):
    def splitString(self, s):
        """
        :type s: str
        :rtype: bool
        """
        def backtrack(stack, substr):
			# Made it to the end of s
            if not substr:
                return True

			# Try all substrings of length 1 to len(substr) for a valid decreasing substring
            for i in range(1, len(substr)+1):

				# Push to stack if no initial value or current substring is one less than previously seen substring
                if not stack or int(substr[:i]) == stack[-1] - 1:
                    stack.append(int(substr[:i]))

					# Made it to the end of s and s can be split into at least two substrings
                    if backtrack(stack, substr[i:]) and len(stack) > 1:
                        return True
                    stack.pop()

            return False

        return backtrack([], s)
