    using ull = unsigned long long;

    bool solve(vector<ull>& path, string& s, int i) {
        if(i == s.size()) return true;

        ull num = 0;

        for(int j=i; j<s.size()-1+(not path.empty()); j++) {
            num = 10*num + (s[j]-'0');

            if(path.empty() or path.back()-num == 1) {
                path.push_back(num);

                if(solve(path, s, j+1)) return true;

                path.pop_back();
            }
        }

        return false;
    }

    bool splitString(string s) {
        vector<ull> path;
        return solve(path, s, 0);
    }
};
