object Solution {
  import math.BigInt
  def splitString(s: String): Boolean = {
    val cache = collection.mutable.Map.empty[Seq[Char],Seq[Seq[Seq[Char]]]]
    val cache2 = collection.mutable.Map.empty[(Seq[Char],Seq[Char]),Boolean]
    val cache3 = collection.mutable.Map.empty[Seq[Char],BigInt]
    def dropZeros(seq: Seq[Char]): Seq[Char] =
      if(seq.size <=1) seq
      else if (seq.head == '0') dropZeros(seq.tail)
      else seq
    def impl(seq:Seq[Char]): Seq[Seq[Seq[Char]]] = {
      if(seq.isEmpty) Seq()
      else if(cache.contains(seq)) cache(seq)
       else {
        val rc = List(List(seq)) ++ (1 until (seq.size)).to(List).flatMap{case i =>
          lazy val (d0,t0) = seq.splitAt(i)
          lazy val (d,t) = (dropZeros(d0),dropZeros(t0))
          impl(t).map(d +: _)
        }
        cache += (seq -> rc)
        rc
      }
    }
    def toBigInt(s: Seq[Char]): BigInt = {
      if(cache3.contains(s)) cache3(s) else {
        lazy val t = s.dropWhile(_ == '0')
        val rc = if(t.isEmpty) BigInt(0) else BigInt(s.mkString)
        cache3 += (s -> rc)
        rc
      }
    }
    def compare2(a: Seq[Char], b: Seq[Char]): Boolean = {
      if(cache2.contains(a,b)) cache2(a,b) else {
        val rc = toBigInt(a) - toBigInt(b) == BigInt(1)
        cache2 += ((a,b) -> rc)
        rc
      }
    }
    def check(seq: Seq[Seq[Char]]): Boolean = 
      (seq.size > 1) && seq.sliding(2,1).forall{l => compare2(l(0),l(1)) }
    lazy val y0 = dropZeros(s) //s.dropWhile(_ == '0')
    if(y0.size<2) false else { 
      impl(y0).exists(check)
    }
  }
}
