package main

import (
	"fmt"
	"strconv"
)

func valid(s string, t string) bool {
	si, _ := strconv.Atoi(s)
	ti, _ := strconv.Atoi(t)
	return si-ti == 1
}

func splitString(s string) bool {
	var dfs func(i int, prev string, cnt int) bool
	dfs = func(i int, prev string, cnt int) bool {

		if i == len(s) && cnt > 1 {
			return true
		}

		for j := i; j < len(s); j++ {
			if prev == "" || valid(prev, s[i:j+1]) {
				if dfs(j+1, s[i:j+1], i+1) {
					return true
				}
			}
		}
		return false
	}
	return dfs(0, "", 0)
}

