from functools import cache
class Solution:
    def splitString(self, s: str) -> bool:
        @cache
        def dfs(prev, s):
            if not s: return True
            res = False
            for i in range(len(s)):
                cur = int(s[:i+1])
                if prev - 1 == cur:
                    res = res or dfs(cur, s[i+1:])
            return res
        for i in range(len(s)-1):
            if dfs(int(s[:i+1]), s[i+1:]): return True
        return False
```

