class Solution {
public:
    bool splitString(string s) {
        string head = "";
        int n = s.size();
        bool mt = false;
        int l = 0;
        for(int i = 0; i < n; i++){
            head = head+s[i];
            if(s[i] != '0') mt = true;
            if(mt) l++;
            if(2*l - 1> n) break;
            long long x = stoll(head);
            long long y = 0;
            long long c = x;
            for(int j = i+1; j < n; j++){
