/**
 * @param {string} s
 * @return {boolean}
 */
var splitString = function(s) {
    if (parseInt(s, 10) < 10) return false

    const stripFirstZeroes = str => {
        while (str[0] === '0' && str.length > 1) {
            str = str.substring(1)
        }
        return str
    }

    s = stripFirstZeroes(s)

    const checkNumValid = (num, str) => {
        if (str.length === 0) return true

        str = stripFirstZeroes(str)

        if (num === 1) return str === '0'

        var nextNum = num - 1
        if (str.startsWith(nextNum.toString())) {
            return checkNumValid(nextNum, str.replace(nextNum.toString(), ''))
        }

        return false
    }

    let firstNum = ''
    for (var i = 0; i < Math.ceil(s.length / 2); i++) {
        firstNum += s[i]
        if (checkNumValid(parseInt(firstNum, 10), s.replace(firstNum, ''))) {
            return true
        }
    }

    return false
};
```

