func backtrack(s string, pos int, prev int) bool {
  if len(s) == pos {
    return true
  }
  var x int
  // We want at least two non-empty substrings, so if we're
  // starting from position 0, we don't want to go till the end.
  end := len(s)
  if pos == 0 {
    end = len(s)-1
  }
  for i := pos; i < end; i++ {
    x = x * 10 + int(s[i]-'0')
    if prev != -1 && x != prev-1 {
      continue
    }
    if backtrack(s, i+1, x) {
      return true
    }
  }
  return false
}
