#define MAX_SPLIT_VAL   10000000000         // at least 2 splits should be there and per split cannot have value greater than 10 digits
bool backtrack(char *s, int len, int idx, long preVal) {

    // success: end of the string which means we have found all
    // valid splits previously
    if(idx >= len) return true;

    char sPart[21] = {'\0'};
    for(int i = idx; i < len; i++) {
        strncpy(sPart, s+idx, i - idx + 1);
        long long cVal = atol(sPart);
        if(cVal >= MAX_SPLIT_VAL) return false;

        // the previous value should be 1 greater than current value
        // and further splits after current should follow same condition
        if(preVal == cVal + 1 && backtrack(s, len, i+1, cVal))
            return true;
    }
    return false;
}

bool splitString(char * s){
    int len = strlen(s);

    if(1 == len) return false;

    char split[21] = {'\0'}, *ptr;
    // check single, double, triple digits
    for(int i = 0; i < len-1; i++) {
        strncpy(split, s, i+1);
        long long val = atol(split);
        if(val >= MAX_SPLIT_VAL) return false;
        // printf("split string is %s val is %ld\n", split, val);
        if(backtrack(s, len, i+1, val)) return true;
    }
    return false;
}
