def split_string(s)
    @path = []
    @result = []
    @string = s
    dfs(0)
    return !@result.empty?
end

def dfs(i)
    if i == @string.length && @path.length > 1
       @result << @path[0..-1] 
       return 
    end
    j = i
    while(j < @string.length)
       @path << @string[i..j]
       if condition 
         dfs(j+1) 
       end
       @path.pop 
       j = j + 1 
    end
end

def condition
   @path.length == 1 || (@path[-1].to_i + 1 == @path[-2].to_i)
end
