function splitString(s: string): boolean {

    let n = s.length;

    function isValid(i, last) {
        if(i>=n) {
            return true;
        }

        for(let j= 1; j<=n; j++) {
            let val = +s.substr(i,j);
            if(val == last -1 && isValid(i+j, val)) {
                return true;
            }
        }
        return false;
    }

    for(let i=1; i<n; i++) {
        if(isValid(i, +s.substr(0,i))) {
            return true;
        }
    }
    return false;
};
