var splitString = function (s, previous) {
    if (s.length === 1) return false;

    for (let i = 1; i < s.length; i++) {
        const first = Number(s.substring(0, i));

        if (first === previous - 1 || previous === undefined) {

            for (let j = i + 1; j <= s.length; j++) {
                const second = Number(s.substring(i, j));

                if (second === first - 1) {
                    if (j === s.length || Number(s.substring(j)) === second - 1) return true;
                    else {
                        const found = splitString(s.substring(j), second);
                        if (found) return true;
                    }
                }
            }
        }
        // if no such substring found return false
        if (i === s.length - 1) return false
    }
};
