	/*
        Returns true if 's' from index 'start' can be split
    */
    public boolean backtrack(String s, int start, long previous) {
        if (start == s.length()) { // Base Case: If we reach end of string, then we found a valid split
            return true;
        }

        StringBuilder current = new StringBuilder();
        int end = s.length() - 1;
        if (previous == -1) // This is needed to ensure that there will always be at two substrings in the split
            end -= 1;

        long val = 0;
        for (int i = start; i <= end; i++) {
            current.append(s.charAt(i)); // Try s[:i], s[:i+1], s[:i+2]....

            val = val*10 + (s.charAt(i) - '0');

            // If s[:i] == previous-1, and s[i+1:] can be split up, then return true
            if ((previous-val == 1 || previous == -1) && backtrack(s, i+1, val)) {
                return true;
            }
        }

        return false;
    }
