class Solution {
    char[] sc;
    public boolean splitString(String s) {
        sc = s.toCharArray();
        for (int i = 0; i < sc.length -1 ; i++) {
            if (dfs(i+1, toLong(0, i))) return true;
        }
        return false;
    }

    boolean dfs(int start, long prev) {
        if (start == sc.length) return true;
        int low = start;
        int high = sc.length - 1;
        while (low <= high) {
            int mid = low + (high - low)/2;
            long curr = toLong(start, mid);
            if (curr + 1 == prev) {
                if (dfs(mid+1, curr)) return true;
                low++;
            } else if (curr > prev) {
                high = mid -1;
            } else {
                low = mid + 1;
            }
        }
        return false;
    }

    long toLong(int start, int end) {
        long curr = 0;
        while (start <= end) {
            curr = curr*10 + Character.getNumericValue(sc[start++]);
        }
        return curr;
    }
}
