class Solution:
    def splitString(self, s: str) -> bool:
        return self.help(s, 0, None)

    def help(self, s, i, last):
        if i == len(s):
            return True

        for j in range(i + 1, len(s) + 1 if last != None else len(s)):
            intVal = int(s[i:j])

            if (last == None or intVal == last - 1) and self.help(s, j, intVal):
                return True

        return False
