I forgoed using the parseInt() method to trim off the leading zero and converting the string to an actual integer.

```
var splitString = function(s) {
    const n = s.length;

    let i = 0;

    while (i < n) {
        if (s.charAt(i) === "0") ++i;
        else break;
    }

    if (i === n) return false;

    const start = i;

    for (let i = start; i < n - 1; ++i) {
        const substr = s.substring(start, i + 1);
        const prevNum = subtractOne(substr);
        const restStr = s.substring(i + 1);

        if (checkRest(restStr, prevNum)) return true;
    }

    return false;


    function checkRest(str, prevNum) {
        const n = str.length;

        if (n === 0) return true;

        for (let i = 0; i < n; ++i) {
            const currNum = str.substring(0, i + 1);

            if (areSame(prevNum, currNum)) {
                if (isZero(currNum) && i < n - 1) continue;

                const nextNum = subtractOne(currNum);
                const restStr = str.substring(i + 1);

                if (checkRest(restStr, nextNum)) return true;
            }
        }

        return false;
    }

    function subtractOne(substr) {
        const digits = substr.split("");

        let i = digits.length - 1;

        while (i >= 0) {
            const dig = digits[i];

            if (dig === "0") {
                digits[i] = "9";
            }
            else {
                digits[i] = parseInt(digits[i]) - 1;
                break;
            }
            i--;
        }

        let res = "";

        i = 0;

        while (i < n) {
            if (digits[i] === "0") ++i;
            else break;
        }

        for (; i < digits.length; ++i) {
            res += digits[i];
        }

        return res;
    }

    function isZero(str) {
        for (let i = 0; i < str.length; ++i) {
            if (str.charAt(i) != "0") return false;
        }

        return true;
    }

    function areSame(num1, num2) {
        const m = num1.length; // We are going to want m to be longer
        const n = num2.length; // We want n to be shorter

        if (m < n) return areSame(num2, num1);

        let i = m - 1; // for num1
        let j = n - 1; // for num2

        while (j >= 0) {
            const dig1 = num1[i];
            const dig2 = num2[j];

            if (dig1 != dig2) return false;
            --i;
            --j;
        }


        while (i >= 0) {
            if (num1[i] != "0") return false;
            --i;
        }

        return true;
    }
};
```

