class Solution:
    def splitString(self, s: str) -> bool:
        L = len(s)
        for i in range(1, L):
            curr = int(str(s[:i]))
            if self.helper(curr - 1, i , s, L) == True:
                return True
        return False

    def helper(self, curr, i, s, L):
        if i >= L :
            return True

        # Get rid of any leading 0s
        for j in range(i, L):
            if s[j] == '0':
                continue
            else:
                break

        # And check if the starting portion matches curr
        if s[j:j + len(str(curr))] != str(curr):
            return False
        return self.helper(curr - 1, j + len(str(curr)), s, L)
