var splitString = function(s) {    
    const help = (i, prev, next) => {
        if (i >= s.length) {
            return parseInt(prev) - parseInt(next) === 1;
        }

        let concat = false;
        if (prev && next && +prev - (+next) === 1) {
            concat = help(i+1, next, s[i]);
        } else if (!prev && next){
            concat = help(i+1, next, s[i]);
        }
        let keep = help(i+1, prev, next+s[i]);

        return concat || keep;
    }
    return help(0, '', '');
};
