class Solution {
    public boolean splitString(String s) {
        long t = 0;//first number, since s might have 20 digits, we use long

        for(int i=0;i<s.length()-1;i++){
            t = t*10+s.charAt(i)-'0';
            if(dfs(s,t,i+1))
                return true;
        }
        return false;
    }

    private boolean dfs(String s, long pre, int k){
        if(k==s.length())
            return true;

        long t=0;
        for(int i=k;i<s.length();i++){
            t = t*10+s.charAt(i)-'0';
            if(pre-1==t&&dfs(s,t,i+1)){
                return true;
            }
        }

        return false;
    }
}


//O(n) possible initial value, and for every initial value, 
//we traverse the whole string in the worse case, which takes
//O(n) complexity. -> O(n^2) total complexity
