class Solution:
    def splitString(self, s: str) -> bool:

        n = len(s);

        @lru_cache(None)
        def dp(i,j):
			#when j hit to the last position, we know we finished partition
            if j == n:
				#if i == 0 means we didn't do partition, we just get whole string from 0-n
                if i == 0:
                    return False;
                return True;

            #initial first lastNum as maximum;
            lastNum = float('inf');
            if j != 0:
                lastNum = int(s[i:j]);

			#iterate the nextNum as [j:k], and k will be the next j if valid
            for k in range(j+1,n+1):
                nextNum = int(s[j:k]);
                # ignore 0 but the last partition is an exception
                if nextNum == 0 and k != n:
                    continue;

				#if the lastNum is init val we can continue partition
                if lastNum == float('inf') or nextNum + 1 == lastNum:
                    subProblem = dp(j,k);
                    if subProblem:
                        return True
                else:
					#since the nextNum already >= lastNum, we don't need to append more char to get larger nextNum
                    if nextNum >= lastNum:
                        break;

            return False;

        return dp(0,0)


