class Solution:
	def getMinSwaps(self, num: str, k: int) -> int:
		nums = list(num)
		for i in range(k):
			l = -1
			for j in range(len(nums) - 2, -1, -1):
				if nums[j] < nums[j + 1]:
					l = j
					break
			if l == -1:
				nums.reverse()
			else:
				r = len(nums) - 1
				for j in range(len(nums) - 1, -1, -1):
					if nums[j] > nums[l]:
						nums[l], nums[j] = nums[j], nums[l]
						l += 1
						while l < r:
							nums[l], nums[r] = nums[r], nums[l]
							l += 1
							r -= 1
						break
		res = 0
		for i in range(len(nums)):
			if nums[i] != num[i]:
				j = i
				while j < len(nums) and nums[i] != num[j]:
					j += 1
				res += j - i
				num = num[:i] + num[j] + num[i:j] + num[j + 1:]
		return res

