class Solution {
public:
    int getMinSwaps(string nums, int k) {

        // we want the kth smallest wonderful string first

        string target = nums;
        while (k--) next_permutation(target.begin(), target.end());

        int i = 0, j = 0;
        int len = nums.length();

        while (i < len &&  nums[i] == target[j]) {
            i++; j++;
        }
        int swapCount = 0;

        while (i < len) {
            j = i;
            while (j < len && nums[i] != target[j]) j++;
            while (j > i) {
                swap(target[j], target[j - 1]);
                j--; swapCount++;
            } i++;

        }

        return swapCount;
    }
};
