class Solution(object):
	def getMinSwaps(self, num, k):
		n=len(num)
		dp={1:1}
		dpindex={}
		def findPermutation(num,k,n):
			d={num[n-1]:1}
			for i in range(n-2,-1,-1):
				current=num[i]
				if(current not in d):
					d[current]=1
				else:
					d[current]+=1
				if(current<num[i+1]):
					break
			current=num[i]    
			for j in range(n-1,i,-1):
				if(k==0):
					return num
				if(current<num[j]):
					num[i]=num[j]
					num[j]=current
					total=1
					d[num[i]]-=1
					for z in d.values():
						if(z>1):
							total*=factorial(z)

					fact=factorial(n-i-1)
					fact=fact/total
					if(fact==1 and k>1):
						return findPermutation(num,k-1,n)

					if(fact<=k):
						k=k-fact
					else:
						snum=list(num)
						index=i+1
						k=k-1
						for m in range(n-1,i,-1):
							num[m]=snum[index]
							index+=1
					return findPermutation(num,k,n)

		def factorial(number):
			if(number in dp):
				return dp[number]
			else:
				ans=number*factorial(number-1)
				dp[number]=ans
				return ans


		def findSwaps(inlist,z,index,n,swapCount):
			if(index==n):
				return swapCount
			current=inlist[index]
			flag=True
			for i in range(index,n):
				if(current==z[i]):
					flag=False
					for j in range(i,index,-1):
						temp=z[j-1]
						z[j-1]=z[j]
						z[j]=temp
						swapCount+=1
					return findSwaps(inlist,z,index+1,n,swapCount)
			if(flag):
				return -1

		intlist=[]
		for char in num:
			intlist.append(int(char))

		inlist=list(intlist)
		z=findPermutation(intlist,k,n)
		ans=findSwaps(inlist,z,0,len(inlist),0)
		return ans

