        for(int i = 0; i < k; ++i){ //k!
            next_permutation(num.begin(), num.end());
        }

        for(int i = 0; i < num.size(); ++i){
            for(int j = 0; j < store.size(); ++j){ //n^2
                if(num[i] == store[j]){
                    arr[j] = i;
                    store[j] = '.';
                    break;
                }
            }
        }

        int inv_count = 0;
        multiset<int> mset;
        mset.insert(arr[num.size()-1]);
        //Finding inversion count

        for(int i = num.size()-2; i >= 0; --i){//nlongn
            if(arr[i] > *mset.begin()){
                auto it = mset.begin();
                while(it != mset.end() and *it < arr[i]){
                    inv_count++, it++;
                }
            }
            mset.insert(arr[i]);
        }

        return inv_count;
    }
};

