        int mid = start + (end - start) / 2;
        int res = getMinSwapsToSort(A, start, mid) + getMinSwapsToSort(A, mid + 1, end);

        // Two pointer count inverted pairs between left and right sorted subarrays
        int[] left = Arrays.copyOfRange(A, start, mid + 1);
        int[] right = Arrays.copyOfRange(A, mid + 1, end + 1);
        int l = 0;
        int r = 0;
        int i = start;
        while (l < left.length || r < right.length) {
            if (r >= right.length || l < left.length && left[l] <= right[r]) {
                res += r; // numbers in the right subarray that's smaller than A[l] is equal to the steps r pointer
                // has advanced
                A[i++] = left[l++];
            } else {
                A[i++] = right[r++];
            }
        }
        return res;
    }
}
