def next_perm(s):
    for i in range(len(s)-2, -1, -1):
        if s[i]<s[i+1]:
            for j in range(i, len(s)):
                if j+1 < len(s) and s[j+1] <= s[i]:
                    break
            return s[:i] + s[j] + ''.join(sorted([s[k] for k in range(i, len(s)) if k != j]))
    return sorted(s)

class Solution:
    def getMinSwaps(self, num: str, k: int) -> int:
        target_num = num
        for _ in range(k):
            target_num = next_perm(target_num)

        target = list(target_num)
        cur = list(num)
        swaps = 0
        for i in range(len(cur)):
            if target[i] == cur[i]: continue

            for j in range(i, len(num)):
                if cur[j] == target[i]:
                    swaps += j-i
                    cur = cur[:i] + [cur[j]] + [cur[x] for x in range(i, len(num)) if x!=j]
                    break
        return swaps
