/**
 * Time Complexity: O(n * (n + k))
 * Space Complexity: O(n)
 * where `n` is the length of the string `num`
 */
class Solution {
 public:
  int getMinSwaps(const string &num, const int k) {
    string target(num);
    for (int i = 0; i < k; ++i) {
      next_permutation(target.begin(), target.end());
    }

    const int n = static_cast<int>(num.size());
    int ret = 0;
    string original(num);
    for (int i = 0; i < n; ++i) {
      int j = i;
      for (; j < n && target[i] != original[j]; ++j) {
      }

      ret += j - i;
      for (; j > i; --j) {
        swap(original[j], original[j - 1]);
      }
    }
    return ret;
  }
};
