```
class Solution
{
    bool nextPermutation(string& n)
    {
        int i(size(n)-1);
        for( ; i and n[i-1]>=n[i]; --i);
        if(auto j=i)
        {
            for( ; j<size(n) and n[j]>n[i-1]; ++j);
            swap(n[i-1], n[j-1]);
            reverse(begin(n)+i, end(n));
        }
        else return false;
        return true;
    }
public:   
    int getMinSwaps(string n, int k)
    {
        auto N = n;
        for( ; k--; nextPermutation(n));
        int out{};
        for(int i{}; i<size(N); ++i)
        {   
            int j{i};
            for( ; j<size(n) and n[j]!=N[i]; ++j);
            for( ; i<j; swap(n[j-1], n[j]), --j, ++out);
        }
        return out;
    }
};
```
