class Solution {
    public int getMinSwaps(String num, int k) {
        int len = num.length();
        int[] nums = new int[len];
        int[] original = new int[len];

        int idx = 0;
        for(char c : num.toCharArray()) {
            int digit = (int) (c - '0');
            nums[idx] = digit;
            original[idx] = digit;
            idx++;
        }

        while (k != 0) {
            nextPermutation(nums);
            k--;
        }

        int minSwaps = 0;

        for(int i = 0; i < len; i++) {
            if(nums[i] != original[i]) {
                int j = i + 1;
                while (j < len) {
                    if(original[j] == nums[i]) {
                        break;
                    }
                    j++;
                }

                while(j > 0 && j != i) {
                    swap(original, j, j - 1);
                    minSwaps++;
                    j--;
                }
            }
        }

        return minSwaps;
    }

    public void nextPermutation(int[] nums) {
        int i = nums.length - 2;
        while (i >= 0 && nums[i + 1] <= nums[i]) {
            i--;
        }
        if (i >= 0) {
            int j = nums.length - 1;
            while (nums[j] <= nums[i]) {
                j--;
            }
            swap(nums, i, j);
        }
        reverse(nums, i + 1);
    }

    private void reverse(int[] nums, int start) {
        int i = start, j = nums.length - 1;
        while (i < j) {
            swap(nums, i, j);
            i++;
            j--;
        }
    }

    private void swap(int[] nums, int i, int j) {
        int temp = nums[i];
        nums[i] = nums[j];
        nums[j] = temp;
    }
}
