var getMinSwaps = function(num, k) {
    const n = num.length;

    let swapDigs = num.split("");

    for (let i = 0; i < k; ++i) {

        for (let j = n - 2; j >= 0; --j) {
            const currDig = swapDigs[j];
            const prevDig = swapDigs[j + 1];

            if (currDig < prevDig) {
                let right = n - 1;

                while (right > j && swapDigs[right] <= currDig) --right;  

                [swapDigs[j], swapDigs[right]] = [swapDigs[right], swapDigs[j]];
                swapDigs = flip(swapDigs, j + 1, n - 1);
                break;
            }
        }
    }

    const origDigs = num.split("");

    let swaps = 0;

    for (let i = 0; i < n; ++i) {
        const dig1 = swapDigs[i];
        const dig2 = origDigs[i];

        if (dig2 != dig1) {
            let j = i + 1;

            while (j < n && origDigs[j] != dig1) j++;

            swaps += (j - i);
            origDigs.splice(j, 1);
            origDigs.splice(i, 0, dig1);
        }
    } 

    return swaps;


    function flip(arr, left, right) {
        while (left < right) {
            [arr[left], arr[right]] = [arr[right], arr[left]];
            left++;
            right--;
        }

        return arr;
    }
};
