class Solution:
    def getMinSwaps(self, num: str, k: int) -> int:
        def findGreatest(nums):
            nums = list(nums)
            # 1 find pivot
            pivot = None
            for i in range(len(nums) - 1, 0, -1):
                if nums[i] > nums[i - 1]:
                    pivot = i - 1
                    break
            # 2 swap with pivot if larger than pivot
            for i in range(len(nums) - 1, pivot, -1):
                if nums[i] > nums[pivot]:
                    nums[i], nums[pivot] = nums[pivot], nums[i]
                    break
            # 3 reverse nums after pivot
            nums[pivot +1: ] = nums[pivot +1: ][::-1]
            return ''.join(nums)

        origin = list(num[::])
        for i in range(k):
            num = findGreatest(num)
        # swap adjacent greedy
        ans, n = 0, len(num)
        for i in range(n):
            j = origin.index(num[i], i)
            ans += j - i
            origin[i:j+1] = [origin[j]] + origin[i:j]
        return ans
