class Solution {
public:
    int getMinSwaps(string num, int k) {
        string s = num;
        //finding kth smallest number that is kth next permutation(next permutation is the next largest number)
        while(k--){
            next_permutation(s.begin(), s.end()); //finding 
        }
        int minSwaps = 0; //for counting the number of swaps
        //traversing in the nums
        for(int i = 0; i<num.size(); i++){
            if(num[i]!= s[i]){ //if the digits of new string are not in their actual position, we need to find their position in the original array.
                int j = i+1; //for finding the index of the current digit of s in the right of the nums
                while(num[j]!=s[i])j++; //we will keep incrementing j until we find it's actual position.
                //as soon as we find the actual position we need to swap them

                while(j>i){
                    swap(num[j], num[j-1]);
                    j--;
                    minSwaps++;

                }
            }
        }
        return minSwaps;

    }
};
