        for(int i=0;i<n;i++){
            if(num[i]!=target[i]){
                int j=i+1;
                while(num[j]!=target[i]){
                    j++;
                }
                for(;j>i;--j){
                    swap(num[j],num[j-1]);
                    count++;
                }
            }
        } 
