    private int count(char[] src, char[] dest) {
        int count = 0;
        for (int i = 0; i < src.length; i++) {
            if (src[i] != dest[i]) {
                for (int j = i + 1; j < src.length; j++) {
                    if (src[j] == dest[i]) {
                        for (int k = j; k > i; k--) {
                            swap(src, k, k - 1);
                            count++;
                        }
                        break;
                    }
                }
            }
        }
        return count;
    }

    private void swap(char[] src, int i, int j) {
        char temp = src[i];
        src[i] = src[j];
        src[j] = temp;
    }
}
