    // find kth Permutation
    // step : 2
    // counts adj swaps

    void NextPermutation(string &s){
        int n = s.size();
        for(int i = n - 2 ; i >= 0 ; i--){
            if(s[i] < s[i + 1]){
                int val = s[i];
                int si = i + 1 , ei = n - 1 , x= 0;
                while(si <= ei){
                    int mid = (si + ei)/2;
                    if(s[mid] == val){
                        ei = mid - 1;
                    }
                    else if(s[mid] > val){
                        x = mid;
                        si = mid + 1;
                    }
                    else{
                        ei = mid - 1;
                    }
                }
                swap(s[i] , s[x]);
                reverse(s.begin() + i + 1 , s.end());
                return;
            }
        }
    }

    int getMinSwaps(string s, int k) {
        string s1 = s;
        while(k>0){
            NextPermutation(s);
            k--;
        }
        string s2 = s;

        int n = s1.size();
        int i = n - 1 , j = n - 1;

        int ans = 0; 

        while(i >= 0){
            while(s2[j]!=s1[i]) j--;
            while(j + 1 < n){
                swap(s2[j] , s2[j+1]);
                ans++;
                j++;
            }
            i--;
            j = i;
            n--;
        }
        return ans;

    }
};

