//next Permutation
void nextPermutation(char* nums, int numsSize){

    for(int i = numsSize - 2 ; i >= 0; i--){
        if(nums[i] < nums[i + 1]){
            qsort(&nums[i+1], numsSize-(i+1), sizeof(char), cmpfunc);
            for(int j = i+1; j < numsSize; j++){
                if(nums[j] > nums[i]){
                    char temp = nums[i];
                    nums[i] = nums[j];
                    nums[j] = temp;   
                    return;
                }
            }      
        }
    }

    qsort(nums, numsSize, sizeof(char), cmpfunc);
}

//
//main function
int getMinSwaps(char * num, int k){
    int len = strlen(num);
    char* copy = malloc( (len+1) * sizeof(char));
    char* temp = malloc( (len+1) * sizeof(char));
    copy[len] = '\0';
    strcpy(copy, num);

    //>>> produce kth number to "char* copy" 
    for(int i = 0; i < k; i++){
        nextPermutation(copy, len);
    }
    //<<<<<

    //>>>> begin count swap times
    int ans = 0;
    for(int i = 0; i < len; i++){
        if(copy[i] == num[i])
            continue;
        for(int j = i+1; j < len; j++){
            if(copy[i] == num[j]){
                strncpy(temp, &num[i] , j-i);
                strncpy(&num[i+1], temp , j-i);
                num[i] = copy[i];   
                ans += (j-i) ;
                break;
            }
        }
    }
    //<<<<<

    free(copy);
    free(temp);
    return ans;
}
