def get_fact(li,limit):
	res,total = 1,sum(li)
	li = sorted([e for e in li if e],reverse = True)
	if not li: 
		return 0
	while len(li)!=1:
		n = li[-1]
		li[-1] -= 1
		if n==1:
			li.pop()
		res = res*total//n
		total -= 1
		if res>limit:
			return None
	return res

