public class MinSwaps {
    public int getMinSwaps(String num, int k) {
        if(num == null || num.length() == 0) return 0;


        int[] nums = Arrays.stream(num.chars().toArray())
                .map(Character::getNumericValue)
                .toArray();

        int[] kthSmallest = nextPermutation(nums.clone(), k);

        return countSteps(nums, kthSmallest);
    }

    /**
     * Count the number of steps required to make second array back to the first array
     * @param first
     * @param second
     * @return
     */
    private int countSteps(int[] first, int[] second) {
        int i = 0, j = 0;
        int result = 0;

        int len = first.length;
        while(i < len){
            j = i;

            while(j < len && second[j] != first[i]){
                j++;
            }

            // This will make the second array back to the original one.
            // while doing so, we'll keep track of how many times we are doing this to reach the original
            // In simple words, bring the number at j-th position to i-th position.
            while(i < j){
                swap(second, j, j - 1);
                j--;
                result++;
            }
            i++;
        }
        return result;
    }

    private int[] nextPermutation(int[] nums, int k) {
        if(nums == null || nums.length == 0) return new int[]{};

        int n = nums.length;

        while(k-- > 0) {

            //Step 1: Find the breach
            int i = n - 2;
            while(i >= 0 && nums[i] >= nums[i + 1]) {
                i--;
            }

            //Step 2: Find the next larger element than the current i
            if(i >= 0) { // for 5 4 3 2 1 (all in descending order), i will go to -1
                int j = n - 1;
                while(j >= 0 && nums[j] <= nums[i]) {
                    j--;
                }
                swap(nums, i, j);
            }

            //Step 3: Reverse the sub-array starting from i + 1
            reverse(nums, i + 1, n - 1);

        }

        return nums;
    }

    private void swap(int[] arr, int l, int r) {
        int temp = arr[l];
        arr[l] = arr[r];
        arr[r] = temp;
    }

    private void reverse(int[] arr, int l, int r) {
        while(l <= r) {
            swap(arr, l, r);
            l++;
            r--;
        }
    }
}

