	var getMinSwaps = function(num, k) {
    let nextPermutation = (nums) => {// Permutation Calculator
        let i,j;
        //start from right and find the index where value is more than its succeeding value
        for(i = nums.length - 2; nums[i]>=nums[i+1] && i>=0; i--);
        //if no such value is present i = -1, already max permutation, next would me min permutation
        if(i>=0){
            //find an index from right where value is greater than value at i
            for(j = nums.length -1; nums[j]<=nums[i]; j--);
            //swap these index values
            [nums[i],nums[j]] = [nums[j],nums[i]];
        }
        i++;
        j = nums.length - 1;
        while(i<j){//reverse the array after index i, to get next permutation
            [nums[i],nums[j]] = [nums[j],nums[i]];
            i++;
            j--;
        }
    }
    let arr = num.split('').map((x) => parseInt(x,10));
    while(k>0){//find kth permutation in place
         nextPermutation(arr);
         k--;
    }
    num = num.split('').map((x) => parseInt(x));

    //Swapping Logic
    let count = 0;
    for(let i=0; i<num.length; i++){
        if(num[i]!=arr[i]){//where the two numbers dont match
            let j= (i+1);
            while(num[j]!=arr[i] && j<num.length) j++;//find actual value in num array
            count+= j-i;//add the difference of indices as these are the number of swaps needed
            num.splice(j, 1);//replace the value in array
            num.splice(i, 0, arr[i]);
        }
    }
    return count;
	};

