public class Solution
{
    public int GetMinSwaps(string num, int k)
    {
        char[] characters = num.ToCharArray();
        for (int index = 0; index < k; index++)
        {
            NextPermutation();
        }

        return GetNumberOfSwaps();

        void NextPermutation()
        {
            int n = characters.Length;
            for (int left = n - 2; left >= 0; left--)
            {
                if (characters[left] >= characters[left + 1]) continue;

                int right = n - 1;
                while (characters[right] <= characters[left])
                {
                    right--;
                }

                (characters[left], characters[right]) = (characters[right], characters[left]);
                Array.Reverse(characters, left + 1, n - left - 1);
                return;
            }
        }

        int GetNumberOfSwaps()
        {
            int numberOfSwaps = 0;
            for (int left = 0; left < num.Length; left++)
            {
                int right = left;
                while (num[left] != characters[right])
                {
                    right++;
                }

                while (right != left)
                {
                    (characters[right - 1], characters[right]) = (characters[right], characters[right - 1]);
                    right--;
                    numberOfSwaps++;
                }
            }

            return numberOfSwaps;
        }
    }
}

