impl Solution {
    pub fn get_min_swaps(num: String, k: i32) -> i32 {
        let mut original: Vec<_> = num.into_bytes();
        let mut permute = original.clone();

        for _ in 0..k {
            Self::next_permutation(&mut permute);
        }

        let mut res = 0;

        for i in 0..original.len() {
            if original[i] == permute[i] {
                continue;
            }

            let mut j = i + 1;
            while original[j] != permute[j] {
                j += 1;
            }

            while j > i {
                original.swap(j - 1, j);
                j -= 1;
                res += 1;
            }
        }

        res
    }

    fn next_permutation(chars: &mut Vec<u8>) {
        let mut index = chars.len() - 1;
        while index > 0 {
            if chars[index] > chars[index - 1] {
                break;
            }

            index -= 1;
        }

        let mut swap_index = chars.len() - 1;
        while swap_index >= index {
            if chars[swap_index] > chars[index - 1] {
                break;
            }

            swap_index -= 1;
        }

        chars.swap(index - 1, swap_index);
        chars[index..].reverse();
    }
}

