var getMinSwaps = function(num, k) {
    let digits = [...num];
    const swap = (idx1, idx2) => {
        [digits[idx1], digits[idx2]] = [digits[idx2], digits[idx1]];
    }

    // Find the next wonderful permutation.
    function permutation() {
        for (let i = digits.length - 1; i >= 1; i--) {
            // Find the first i that is greater than i - 1.
            // When that i is found, this means all digits from i to the end are in descending order.
            if (digits[i] > digits[i - 1]) {
                // Swap the i - 1 digit with the smallest digit larger than i - 1 (see below).
                swap(i - 1, nextLarger(i, digits[i - 1]));
                reverse(i, digits.length - 1);
                return;
            }
        }
    }

    // Since all digits from idx onwards are in descending order, 
	// find and return the index of the last number that is greater than the currentVal (which represents the value of i - 1).
    const nextLarger = (idx, currentVal) => {
        let nextLargerIdx = idx;
        for (let i = idx; i < digits.length; i++) {
            if (digits[i] > currentVal) {
                nextLargerIdx = i;
            } else break;
        }
        return nextLargerIdx;
    }

    // Once nextLarger is found, swap i - 1 with the nextLarger index. 
    // This way, the new i - 1 value is guaranteed to be the next smallest digit larger than the old i - 1 value.
    // Moreover, the rest of the array from i to the end is still in descending order.
    // Now reverse the rest of the array so that it will be in ascending order.
    const reverse = (start, end) => {
        while (start < end) {
            swap(start, end);
            start++, end--;
        }
    }

    // Find the next wonderful permutation k times.
    while (k--) permutation();

    // Count the number of swaps.
    let swapCount = 0;
    for (let i = 0; i < num.length; i++) {
        if (num[i] != digits[i]) {
            let j = i;
            while (num[i] != digits[j]) j++;
            while (i < j) {
                swap(j, j - 1);
                j--;
                swapCount++;
            }
        }
    }
    return swapCount;
};
