class Solution {
  public:
    int getMinSwaps(string num, int k) {
      string after = num;
      while (k--)
        nextPermutation(after);
      int ret = 0;
      while (num != after) {
        for (int i = 0; i < num.size(); i++) {
          if (num[i] == after[i]) // found not match pos
            continue;
          for (int j = i+1; j < num.size(); j++) {
            // move num[j] backward to num[i], finish one
            if (num[j] == after[i]) {
              for (int m = j; m > i; m--) {
                ret++;
                swap(num[m], num[m-1]);
              }
              break;
            }
          }
          break;
        }
      }
      return ret;
    }

    void nextPermutation(string& num) {
      int i = num.size()-1;
      while (i > 0 && num[i-1] >= num[i])
        i--;
      for (int j = num.size()-1; i > 0 && j >= 0; j--) {
        if (num[j] > num[i-1]) {
          swap(num[j], num[i-1]);
          break;
        }
      }
      reverse(num.begin()+i, num.end());
    }
};
