        for(int p = 0; p < k; p++){

            int i = len - 2;

            while(i >= 0 && numbers[i] >= numbers[i+1]){
                i--;
            }

            if(i >= 0){
                int j = len - 1;
                while(numbers[i] >= numbers[j]){
                    j--;
                }
                swap(i, j);
            }

            reverse(i + 1);
        }
    }

    int countSteps(String num){

        int i = 0, j = 0;
        int result = 0;

        while(i < len){
            j = i;

            while(j < len && numbers[j] != num.charAt(i)){
                j++;
            }

            while(i < j){
                swap(j, j - 1);
                j--;
                result++;
            }
            i++;
        }
        return result;
    }

    void reverse(int start){
        int s = start, e = len - 1;
        while(s < e){
            swap(s, e);
            s++;
            e--;
        }
