class Solution {
public:
    int getMinSwaps(string num, int k) {
        int n = num.length();
        string temp = num;

        while(k--) {
            int id = n - 2;
            char prev = num[n - 1];
            map<char, int> mp;
            mp[prev] = n - 1;
            while (id >= 0 && prev <= num[id]) {
                //cout << id << ":" << num[id] << endl;
                mp[num[id]] = id;
                prev = num[id];
                id--;
            }
            auto it = mp.upper_bound(num[id]);
            swap(num[id], num[it->second]);
            sort(num.begin() + id + 1, num.end());
            //cout << k << ":" << num << endl;
        }

        int id = 0, count = 0;
        for (; id < num.length(); id++) {
            if (temp[id] != num[id])   break;
        }

        for (int i = id; i < num.size(); i++) {
            //cout << nums[i] << endl;
            auto it = find(temp.begin() + i, temp.end(), num[i]);
            count += it - (temp.begin() + i);
            temp.erase(it);
            temp.insert(temp.begin() + i, num[id]);
        }
        return count;
    }
};
