func next(s string) string {
    var res []byte
    var list []byte
    for i := len(s) - 1; i >= 1; i-- {
        list = append(list, s[i])
        if s[i] > s[i - 1] {
            list = append(list, s[i - 1])
            res = []byte(s[:i - 1])
            sort.Slice(list, func(i int, j int) bool {
                return list[i] < list[j]
            })

            tar := sort.Search(len(list), func(j int) bool {
                return list[j] > s[i - 1]
            })
            res = append(res, list[tar])
            res = append(res, list[:tar]...)
            res = append(res, list[tar + 1:]...)
            break
        }
    }
    return string(res)
}

func swap(a string, b string) int {
    for i := 0; i < len(a); i++ {
        if a[i] != b[i] {
            for j := i + 1; j < len(b); j++ {
                if b[j] == a[i] {
                    return j - i + swap(a[i + 1:], b[i:j] + b[j + 1:])
                }
            }
        }
    }
    return 0
}
